import { CoffeeDto } from './dto/coffee.dto'
import { UpdateCoffeeDto } from './dto/update-coffee.dto'

export class CoffeeApi {
  public static async getAll(): Promise<CoffeeDto[]> {
    const resp = await fetch('http://localhost:4000/coffees/', {
      method: 'GET',
    })

    return await resp.json()
  }

  public static async updateOne(restaurantId: number, updateRequest: UpdateCoffeeDto): Promise<any> {
    const resp = await fetch(`http://localhost:4000/coffees/${coffeeId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updateRequest),
    })

    return await resp.json()
  }
}
