import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react'
import React, { Component } from 'react'

const mapStyles = {
  width: '100%',
  height: '100%',
}

export class MapContainer extends Component<{ google: any }> {
  state = {
    showInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
  }

  onMarkClick = (props, marker: any, e: any) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showInfoWindow: true,
    })

  onClose = (props) => {
    if (this.state.showInfoWindow) {
      this.setState({
        showInfoWindow: false,
        activeMarker: null,
      })
    }
  }

  render(): any {
    return <Map google={this.props.google} style={mapStyles} initialCenter={{ lat: 47.95497, lng: 22.3261 }} />
  }
}

export default GoogleApiWrapper({ apiKey: 'AIzaSyBmJMPAWVynDTMS389rYx650rDG6-Syb_c' })(MapContainer)
