import React, { useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faShoppingCart, faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { CoffeeApi } from '../api/coffee.api'
import { CoffeeDto } from '../api/dto/coffee.dto'

const Card: React.FunctionComponent = () => {
  let [coffees, setCoffees] = useState<CoffeeDto[]>([])
  useEffect(() => {
    async function fetchAll(): Promise<void> {
      const resp = await CoffeeApi.getAll()

      setCoffees(resp)
    }
    fetchAll().then(() => {
      console.log('ok')
    })
  }, [])

  const [input, setInput] = useState('')
  const handleChange = (e: any): void => {
    e.preventDefault()
    setInput(e.target.value)
  }

  if (input.length > 0) {
    coffees = coffees.filter((i) => {
      return i.name.toLowerCase().match(input) || i.name.toUpperCase().match(input) || i.name.match(input)
    })
  }

  const itemsArray: any = []

  if (itemsArray.length === 0) {
    localStorage.setItem('items', JSON.stringify(itemsArray))
  }

  const sortAsc = (): any => {
    const sorted = [...coffees].sort((a, b) => {
      return a.name.localeCompare(b.name)
    })
    setCoffees(sorted)
  }

  const sortDesc = (): any => {
    const sorted = [...coffees].sort((a, b) => {
      return b.name.localeCompare(a.name)
    })
    setCoffees(sorted)
  }

  const sortPriceAsc = (): any => {
    const sorted = [...coffees].sort((a, b) => {
      return a.price - b.price
    })
    setCoffees(sorted)
  }

  const sortPriceDesc = (): any => {
    const sorted = [...coffees].sort((a, b) => {
      return b.price - a.price
    })
    setCoffees(sorted)
  }

  const [passwordShown, setPasswordShown] = useState(false)

  if (itemsArray.length > 0) {
    setPasswordShown(!passwordShown)
  }

  return (
    <main className="mb-8 pb-8">
      <div className="relative mt-6 max-w-sm sm:max-w-sm md:max-w-md lg:max-w-lg xl:max-w-xl mx-auto">
        <span className="absolute inset-y-0 left-0 pl-3 flex items-center">
          <FontAwesomeIcon className="text-gray-500" icon={faSearch} />
        </span>
        <input
          className="w-full border rounded py-2
                   focus:border-gray-700 focus:outline-none focus:outline-none text-center"
          type="text"
          placeholder="Search"
          onChange={handleChange}
          value={input}
        />
      </div>
      <div className="flex justify-center mx-auto mt-6 max-w-sm sm:max-w-sm md:max-w-md lg:max-w-lg xl:max-w-xl">
        <p className="mr-2 rounded text-gray-600 px-2 py-1 w-20 focus:outline-none">Sort by:</p>
        <button
          onClick={sortAsc}
          className={
            passwordShown
              ? 'mr-8 rounded border border-gray-400 text-gray-600 px-2 py-1 w-16 focus:outline-none'
              : 'mr-8 rounded border border-yellow-600 text-gray-600 px-2 py-1 w-16 focus:outline-none'
          }
        >
          A-Z
        </button>
        <button onClick={sortDesc} className="mr-8 rounded border border-gray-400 text-gray-600 px-2 py-1 w-16 focus:outline-none">
          Z-A
        </button>
        <p className="rounded text-gray-600 px-2 py-1 w-20 focus:outline-none">Price:</p>
        <button onClick={sortPriceAsc} className="mr-8 rounded border border-gray-400 text-gray-600 px-2 py-1 w-16 focus:outline-none">
          <FontAwesomeIcon className="text-gray-500" icon={faArrowUp} />
        </button>
        <button onClick={sortPriceDesc} className="rounded border border-gray-400 text-gray-600 px-2 py-1 w-16 focus:outline-none">
          <FontAwesomeIcon className="text-gray-500" icon={faArrowDown} />
        </button>
      </div>
      <div className="container mx-auto px-6 pt-8">
        <h1 id="rf" className="text-gray-800 text-4xl font-medium cursor-default">
          Coffees
        </h1>
        <span className="mt-3 text-base text-gray-600 pb-8">Buy our coffees so you donâ€™t have to be without them at home either.</span>
        <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 mt-6">
          {coffees
            .filter((coffee) => coffee.order)
            .map((coffee) => (
              <div
                className="w-full max-w-sm mx-auto rounded shadow
                                     overflow-hidden bg-gray-200"
                key={coffee.id}
              >
                <div
                  className="flex items-end justify-end h-56 w-full bg-cover
                                         bg-center bg-no-repeat"
                  style={{ backgroundImage: `url(${coffee.image})` }}
                >
                  <button
                    className="bg-yellow-600 p-2 rounded-full h-12 w-12 text-white mx-5 -mb-6 hover:bg-yellow-500 focus:outline-none"
                    onClick={(): any => {
                      itemsArray.push(coffee)
                      localStorage.setItem('items', JSON.stringify(itemsArray))
                    }}
                  >
                    <FontAwesomeIcon size="sm" icon={faShoppingCart} />
                  </button>
                </div>
                <div className="px-5 py-3 bg-white">
                  <h3 id="rf" className="text-gray-800 text-xl font-bold cursor-default">
                    {coffee.name}
                  </h3>
                  <span className="text-gray-700 text-lg mt-2 cursor-default">
                    {coffee.price} Ft
                    <br />
                  </span>
                  <span className="text-gray-600 mt-2 cursor-default">{coffee.description}</span>
                </div>
              </div>
            ))}
        </div>
      </div>
    </main>
  )
}

export default Card
