import React, { useState } from 'react'

const Input: React.FunctionComponent<{ value: any }> = ({ value }) => {
  const [count, setCount] = useState(1 as any)

  return (
    <input
      type="number"
      min="1"
      value={count}
      className="w-full text-center text-gray-700 bg-gray-200 outline-none focus:outline-none hover:text-black focus:text-black"
      onChange={(event): any => {
        setCount(event.target.value)
      }}
    />
  )
}

export default Input
