import React, { useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import { CoffeeApi } from '../api/coffee.api'
import { CoffeeDto } from '../api/dto/coffee.dto'

const Card: React.FunctionComponent = () => {
  let [coffees, setCoffees] = useState<CoffeeDto[]>([])
  useEffect(() => {
    async function fetchAll(): Promise<void> {
      const resp = await CoffeeApi.getAll()

      setCoffees(resp)
    }
    fetchAll().then(() => {
      console.log('ok')
    })
  }, [])

  const [input, setInput] = useState('')
  const handleChange = (e: any): void => {
    e.preventDefault()
    setInput(e.target.value)
  }

  if (input.length > 0) {
    coffees = coffees.filter((i) => {
      return i.name.toLowerCase().match(input) || i.name.toUpperCase().match(input) || i.name.match(input)
    })
  }

  const itemsArray: any = []

  localStorage.setItem('items', JSON.stringify(itemsArray))

  return (
    <main className="mb-8 pb-8">
      <div className="relative mt-6 max-w-lg sm:mx-8">
        <span className="absolute inset-y-0 left-0 pl-3 flex items-center">
          <FontAwesomeIcon className="text-gray-500" icon={faSearch} />
        </span>
        <input
          className="w-full border rounded-md pr-8 py-2
                   focus:border-gray-700 focus:outline-none focus:outline-none text-center"
          type="text"
          placeholder="Search"
          onChange={handleChange}
          value={input}
        />
      </div>
      <div className="container mx-auto px-6 pt-8">
        <h3 id="rf" className="text-gray-700 text-3xl font-bold cursor-default">
          Coffees
        </h3>
        <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 mt-6">
          {coffees.map((coffee) => (
            <div
              className="w-full max-w-sm mx-auto rounded shadow
                                     overflow-hidden bg-gray-200"
              key={coffee.id}
            >
              <div
                className="flex items-end justify-end h-56 w-full bg-contain
                                         bg-center bg-no-repeat"
                style={{ backgroundImage: `url(${coffee.image})` }}
              >
                <button
                  className="bg-yellow-600 p-2 rounded-full h-10 w-10 text-white mx-5 -mb-6 hover:bg-yellow-500 focus:outline-none"
                  onClick={(): any => {
                    itemsArray.push(coffee)
                    localStorage.setItem('items', JSON.stringify(itemsArray))
                  }}
                >
                  <FontAwesomeIcon size="sm" icon={faShoppingCart} />
                </button>
              </div>
              <div className="px-5 py-3 bg-white">
                <h3 id="rf" className="text-gray-700 font-bold cursor-default">
                  {coffee.name}
                </h3>
                <span className="text-gray-500 mt-2 cursor-default">
                  {coffee.price} Ft
                  <br />
                </span>
                <span className="text-gray-500 mt-2 cursor-default">{coffee.weight} g</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </main>
  )
}

export default Card
